body.script:
function get_ready(id){
    const checkBox_ids = ["checkbox_con", "checkbox_ins", "checkbox_tut"];
    const all_in_tutorial = document.querySelectorAll('[id^="dropdownT"]');

    if (document.getElementById(id).checked){
         all_in_tutorial.forEach(element => {element.required = false});
        checkBox_ids.forEach(e_id => {
          var element = document.getElementById(e_id);
          element.checked = true;
          element.disabled=true;
          var pop_element = document.getElementById(e_id.concat("_next"));
          pop_element.innerHTML = "";});

    }
    else{
        checkBox_ids.forEach(e_id => {
          var element = document.getElementById(e_id);
          element.checked = false;
          element.disabled=false;
          var pop_element = document.getElementById(e_id.concat("_next"));
          pop_element.innerHTML = "";});
        all_in_tutorial.forEach(element => {element.required = true});
    }
    next(id);
}

function next(id){
    var pop_element = document.getElementById(id.concat("_next"));
    if (document.getElementById(id).checked){
        switch(id) {
        case "checkbox_all":
            pop_element.innerHTML = 'Great, you can go to the last tab directly.';
            break;
        case "checkbox_con":
            pop_element.innerHTML = 'Thanks, please go to the next tab to read the instruction.';
            break;
        case "checkbox_ins":
            pop_element.innerHTML = 'Please go to the next tab to take the tutorial.';
            break;
        case "checkbox_tut":
            pop_element.innerHTML = 'Please go to the next tab for HIT.';
        }
    }
    else{
        pop_element.innerHTML = "";
    }
}

