function follow_up(id){
    const ques_num = id.split('dropdown')[1];
    const conf_num = String(parseInt(ques_num)+1);
    const array1 = ["0", "1"];
    var question_title =document.getElementById("qt".concat(ques_num).concat("_r"));
    var question_option =document.getElementById("dropdown".concat(ques_num).concat("_r"));
    var confidence_title =document.getElementById("qt".concat(conf_num));
    var confidence_option =document.getElementById("dropdown".concat(conf_num));

    if (document.getElementById(id).value ==="2"){
        question_title.hidden = false;
        question_option.hidden = false;
        question_option.value = "";
        question_option.required = true;
        confidence_title.hidden = true;
        confidence_option.hidden = true;
        confidence_option.value = "";
        confidence_option.required = false;
    }
    else if (array1.includes(document.getElementById(id).value)){
        question_title.hidden = true;
        question_option.hidden = true;
        question_option.required = false;
        confidence_title.hidden = false;
        confidence_option.hidden = false;
        confidence_option.value = "";
        question_option.value = "";
        confidence_option.required = true;
    }
}

function showAlert() {
   // Get the checkbox
  var checkBox = document.getElementById("checkbox_all");
  const checkBox_ids = ["checkbox_con", "checkbox_ins", "checkbox_tut"];
     if (!checkBox.checked){
        flag = true;
        for(let i=0; i<checkBox_ids.length; i++){
            if (document.getElementById(checkBox_ids[i]).checked === false ){
              flag = false;
            }
        }
        if (flag === false){
            var myText = "Sorry, your submission has failed because you have not checked all required checked boxes. Please go to the 'ReadMe' page and follow the guidance in the section 'Which applies to you', thanks.";
            alert (myText);
        }
    }
 }
